mapper.select:
SELECT ch.*, c.id c_id, c.name c_name, c.amount c_amount, c.min_point c_min_point, c.platform c_platform, c.start_time c_start_time, c.end_time c_end_time, c.note c_note, c.use_type c_use_type, c.type c_type, c.count c_count, c.per_limit c_per_limit, c.publish_count c_publish_count, c.use_count c_use_count, c.receive_count c_receive_count, c.enable_time c_enable_time, c.code c_code, c.member_level c_member_level, cpr.id cpr_id, cpr.product_id cpr_product_id, cpr.coupon_id cpr_coupon_id, cpr.product_name cpr_product_name, cpr.product_sn cpr_product_sn, cpcr.id cpcr_id, cpcr.coupon_id cpcr_coupon_id, cpcr.product_category_name cpcr_product_category_name, cpcr.parent_category_name cpcr_parent_category_name, cpcr.product_category_id cpcr_product_category_id FROM sms_coupon_history ch LEFT JOIN sms_coupon c ON ch.coupon_id = c.id LEFT JOIN sms_coupon_product_relation cpr ON cpr.coupon_id = c.id LEFT JOIN sms_coupon_product_category_relation cpcr ON cpcr.coupon_id = c.id
now() >c.start_time and c.end_time>now()
AND ch.member_id = #{memberId}
AND ch.use_status = #{useStatus}
ORDER BY c.amount DESC
