SELECT T.`STATUS` AS ORDER_STATUS,T.ID AS ORDER_ID,T1.ICON,T1.NICKNAME,T1.PERSONALIZED_SIGNATURE,T1.PHONE,T2.ICON AS MATCHER_ICON, T2.NICK_NAME AS MATCHER_NICK_NAME,T2.`NAME` AS MATCHER_NAME,T.PAY_AMOUNT, T5.PROFIT_TYPE,T5.PROFIT,T.CREATE_TIME AS ORDER_CREATE_TIME,T4.NICK_NAME AS INVITE_NICK_NAME,T4.ICON AS INVITE_ICON, T4.`NAME` AS INVITE_NAME,T5.`STATUS`,T5.ID FROM OMS_ORDER T LEFT JOIN UMS_MEMBER T1 ON T.MEMBER_ID = T1.ID LEFT JOIN SYS_USER T2 ON T1.MATCH_USER_ID = T2.ID LEFT JOIN UMS_APPLY_MATCHER T3 ON T3.PHONE = T2.PHONE AND T3.AUDIT_STATUS = '1' LEFT JOIN SYS_USER T4 ON T4.PHONE = T3.INVITE_PHONE LEFT JOIN OMS_MATCHER_COMMISSION T5 ON T5.ORDER_ID = T.ID WHERE T.`STATUS` IN('1','2','3')
AND T.ID = #{matcherCommission.orderId}
AND T.CREATE_TIME > #{matcherCommission.startOrderDate}
AND T.CREATE_TIME <= #{matcherCommission.endOrderDate}
AND (T1.NICKNAME LIKE CONCAT('%',#{matcherCommission.nickname},'%') OR T1.PERSONALIZED_SIGNATURE LIKE CONCAT('%',#{matcherCommission.nickname},'%'))
AND (T2.NICK_NAME LIKE CONCAT('%',#{matcherCommission.matcherName},'%') OR T2.`NAME` LIKE CONCAT('%',#{matcherCommission.matcherName},'%') OR T4.NICK_NAME LIKE CONCAT('%',#{matcherCommission.matcherName},'%') OR T4.`NAME` LIKE CONCAT('%',#{matcherCommission.matcherName},'%'))
AND T5.`STATUS` = #{matcherCommission.status}
AND T5.MATCHER_USER_ID = #{matcherCommission.matcherUserId}

SELECT T.`STATUS` AS ORDER_STATUS,T.ID AS ORDER_ID,T1.ICON,T1.NICKNAME,T1.PERSONALIZED_SIGNATURE,T1.PHONE,T2.ICON AS MATCHER_ICON, T2.NICK_NAME AS MATCHER_NICK_NAME,T2.`NAME` AS MATCHER_NAME,T.PAY_AMOUNT, T5.PROFIT_TYPE,T5.PROFIT,T.CREATE_TIME AS ORDER_CREATE_TIME,T4.NICK_NAME AS INVITE_NICK_NAME,T4.ICON AS INVITE_ICON, T4.`NAME` AS INVITE_NAME,T5.`STATUS`,T5.ID FROM OMS_ORDER T LEFT JOIN UMS_MEMBER T1 ON T.MEMBER_ID = T1.ID LEFT JOIN SYS_USER T2 ON T1.MATCH_USER_ID = T2.ID LEFT JOIN UMS_APPLY_MATCHER T3 ON T3.PHONE = T2.PHONE AND T3.AUDIT_STATUS = '1' LEFT JOIN SYS_USER T4 ON T4.PHONE = T3.INVITE_PHONE LEFT JOIN OMS_MATCHER_COMMISSION T5 ON T5.ORDER_ID = T.ID
T.`STATUS` IN('1','2','3')
AND T.ID = #{matcherCommission.orderId}
AND T.CREATE_TIME > #{matcherCommission.startOrderDate}
AND T.CREATE_TIME <= #{matcherCommission.endOrderDate}
AND (T1.NICKNAME LIKE CONCAT('%',#{matcherCommission.nickname},'%') OR T1.PERSONALIZED_SIGNATURE LIKE CONCAT('%',#{matcherCommission.nickname},'%'))
AND (T2.NICK_NAME LIKE CONCAT('%',#{matcherCommission.matcherName},'%') OR T2.`NAME` LIKE CONCAT('%',#{matcherCommission.matcherName},'%') OR T4.NICK_NAME LIKE CONCAT('%',#{matcherCommission.matcherName},'%') OR T4.`NAME` LIKE CONCAT('%',#{matcherCommission.matcherName},'%'))
AND T5.`STATUS` = #{matcherCommission.status}
AND T5.MATCHER_USER_ID = #{matcherCommission.matcherUserId}
AND T5.`STATUS` IN
#{status}
